<script>
    function formatPhoneNumber(str) {
        //Filter only numbers from the input
        let cleaned = ('' + str).replace(/\D/g, '');
        
        //Check if the input is of correct length
        let match = cleaned.match(/^(\d{1})(\d{3})(\d{3})(\d{2})(\d{2})$/);
    
        if (match) {
            return  '+' + match[1] + ' ' + match[2] + ' ' + match[3] + '-' + match[4]  + '-' + match[5] 
        };
    
        return null
    };

    function calculateAge(birthday) { // birthday is a date
        let ageDifMs = Date.now() - birthday.getTime();
        let ageDate = new Date(ageDifMs); // miliseconds from epoch
        return Math.abs(ageDate.getUTCFullYear() - 1970);
    }

	export default {
		props: ['profile'],
		data: function() {
			return {
                declinationOfYears: 'лет',
			}
		},
		created: function() {
            let { first_name, last_name, phone, date_of_birth } = this._props.profile;
            this.first_name = first_name;
            this.last_name = last_name;
            this.phone = formatPhoneNumber(phone);
            
            date_of_birth = date_of_birth.replace(/-/gi, '/'); // Формат 1997-05-10 не читается в Safari браузерах, трансформируем дату в формат 1997/05/10
            this.age = calculateAge(new Date(date_of_birth));
            if (this.age % 10 == 1) {
                this.declinationOfYears = 'год'
            }
            if (this.age % 10 > 1 && this.age % 10 < 5) {
                this.declinationOfYears = 'года'
            }
		},
	}
</script>

